import React, {useState} from 'react'
import {Button} from 'react-bootstrap'

const IndChar = ({ character }) => {
    // console.log(character)

    let favCharacters = {
        name: character.name 
      }


      let config ={
        method: "POST",
        headers: {
          "Content-type": "application/json",
          "Accepts": "application/json"
        },
        body: JSON.stringify()
      }



    const [isFavorite ,setFavorite] = useState(false)
    const handleButton = () => {
        setFavorite(true)
    }

    return (
        <div className='char-card-container'>
            <div className='char-card'>
                <div className='char-card-front'>
                    <img className="char-image" src={character.img} alt="" />
                </div>
                <div className='char-card-back'>
                    <section>
                        <h3><strong>{character.name}</strong></h3>
                        {/* <hr className="line"/> */}
                        <h4>Nickname: {character.nickname}</h4>
                        <h4>Portrayed By: {character.portrayed}</h4>
                        <h4>Status: {character.status}</h4>
                        <h4>Occupation(s): {character.occupation.join(', ')}</h4>
                        <Button className="charFavBtn" onClick={(!isFavorite) ? handleButton : null} variant="warning">{isFavorite ? 'Added to Favorites' : 'Add to Favorites'}</Button>
                    </section>
                </div>
            </div>
        </div>
    )
}

export default IndChar
