import React, {useState, useEffect} from 'react'
import '../App.css';

const Home = () => {

    const [char, setChar] = useState([])
    const [quote, setQuote] = useState([])

    useEffect(() => {
        fetch(`http://localhost:3000/favCharacters`)
        .then(res => res.json())
        .then(data => setChar(data))
        // .then(data => console.log(data))
    }, [])

    useEffect(() => {
        fetch(`http://localhost:3000/favQuotes`)
        .then(res => res.json())
        .then(data => setQuote(data))
        // .then(data => console.log(data))
    }, [])

const handleDelete = () => {

    let config = {
        method: "DELETE",
        headers: {
            "Content-type": "application/json",
            "Accepts": "application/json"
        }
    }
    fetch(`http://localhost:3000/favCharacters`, config)
        .then(res => res.json())
        .then(data => setChar(data))
} 


    return (
        <div className="home-page-container">
            <h1>Welcome to Breaking Bad Quick Facts & Quotes</h1>
            <div className="home-page-elements">
                <div className="character-column">
                    <h2>Favorite Characters</h2>
                        <div className="fav-char">
                            {char.map(c => {
                                return <section>
                                    <h3>{c.name}</h3>
                                    <img src={c.image} alt="CharPic"/>
                                    <button onClick={handleDelete}>X</button>
                                </section>
                            })}
                        </div>
                </div>
                <div className="quote-column">
                    <h2>Favorite Quotes</h2>
                        <div className="fav-quote">
                            {quote.map(q => {
                                return <ul>
                                    <li>{q.quote}</li>
                                    <li> - {q.author}</li>
                                </ul>
                            })}
                        </div>
                </div>
            </div>
        </div>
    )
}

export default Home
